mapper.select:
SELECT lcr.*, lca.article_creater_id AS publishUserId, lca.creator AS publishUserName, IFNULL(lca.article_title,lca.article_content) reportFlagName FROM lmshop_cms_report lcr LEFT JOIN lmshop_cms_article lca ON lcr.report_flag_id = lca.id
lcr.del_flag = 0
AND lcr.report_status = #{params.reportStatus}
AND instr(lcr.report_content,#{params.reportContent}) > 0
and (instr(lca.article_title,#{params.reportFlagName}) > 0 or instr(lca.article_content,#{params.reportFlagName}) > 0)
AND instr(lcr.creator,#{params.creator}) > 0
AND lcr.create_date >= #{params.beginTime}
AND lcr.create_date <= #{params.endTime}
ORDER BY lcr.create_date desc
